    private void b4MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_b4MouseClicked
        // TODO add your handling code here:
    if(Integer.parseInt(t1.getText())>=Integer.parseInt(t2.getText())){
    if(Integer.parseInt(t1.getText())>=Integer.parseInt(t3.getText())){
    if(Integer.parseInt(t1.getText())>=Integer.parseInt(t4.getText())){
    if(Integer.parseInt(t1.getText())>=Integer.parseInt(t5.getText()))  {t7.setText(t1.getText());} 
      else{t7.setText(t5.getText());}}}
    }
    
    else{if(Integer.parseInt(t2.getText())>=Integer.parseInt(t3.getText()))
        {if(Integer.parseInt(t2.getText())>=Integer.parseInt(t4.getText()))
        {if(Integer.parseInt(t2.getText())>=Integer.parseInt(t5.getText())) {t7.setText(t2.getText());}
          else{t7.setText(t5.getText());}}} 
    
    else{if(Integer.parseInt(t3.getText())>=Integer.parseInt(t4.getText()))
        {if(Integer.parseInt(t3.getText())>=Integer.parseInt(t5.getText()))  {t7.setText(t3.getText());}
           else{t7.setText(t5.getText());}}
    else{if(Integer.parseInt(t4.getText())>=Integer.parseInt(t5.getText()))  {t7.setText(t4.getText());}
           else{t7.setText(t5.getText());}}}
    }
    }//GEN-LAST:event_b4MouseClicked

    private void b6MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_b6MouseClicked
        // TODO add your handling code here:
        int winner1;
        int winner2;
        if(Integer.parseInt(t1.getText())>=Integer.parseInt(t2.getText())){winner1=Integer.parseInt(t1.getText());}
        else{winner1=Integer.parseInt(t2.getText());}
        
        if(Integer.parseInt(t3.getText())>=Integer.parseInt(t4.getText())){winner2=Integer.parseInt(t3.getText());}
        else{winner2=Integer.parseInt(t4.getText());}
        
        if(winner1>=winner2){t5.setText(Integer.toString(winner1));}
        else{t5.setText(Integer.toString(winner2));}
    }//GEN-LAST:event_b6MouseClicked

