    private void b5MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_b5MouseClicked
        // TODO add your handling code here:
        int winner1;
        int winner2; 
        int winner3;
        if(Integer.parseInt(t1.getText())>=Integer.parseInt(t2.getText())){winner1=Integer.parseInt(t1.getText());}
        else{winner1=Integer.parseInt(t2.getText());}
        
        if(Integer.parseInt(t3.getText())>=Integer.parseInt(t4.getText())){winner2=Integer.parseInt(t3.getText());}
        else{winner2=Integer.parseInt(t4.getText());}
        
        if(Integer.parseInt(t5.getText())>=Integer.parseInt(t6.getText())){winner3=Integer.parseInt(t5.getText());}
        else{winner3=Integer.parseInt(t6.getText());}
          
        if(winner1>=winner2){
            if(winner1>=winner3){t7.setText(Integer.toString(winner1));}
                else{t7.setText(Integer.toString(winner3));}}
        else{if(winner2>=winner3){t7.setText(Integer.toString(winner2));}
                else{t7.setText(Integer.toString(winner3));}}
    }//GEN-LAST:event_b5MouseClicked

